import React, { useEffect, useState } from 'react';
import Board from './components/Board';
import './App.css';

const App = () => {
  const [gameState, setGameState] = useState(null);

  useEffect(() => {
    // Conectar ao WebSocket
    const ws = new WebSocket('ws://localhost:8080');

    ws.onmessage = (message) => {
      const { type, game } = JSON.parse(message.data);
      if (type === 'update') {
        setGameState(game);
      }
    };

    ws.onopen = () => {
      // Entrar no jogo
      ws.send(JSON.stringify({ type: 'join', data: { gameId: 'default' } }));
    };

    return () => {
      ws.close();
    };
  }, []);

  return (
    <div className="App">
      <h1>Chess Game</h1>
      {gameState && <Board gameState={gameState} />}
    </div>
  );
};

export default App;
