import React from 'react';
import { useDrag } from 'react-dnd';
import './Piece.css';

const Piece = ({ piece }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'PIECE',
    item: { type: piece.constructor.name.toLowerCase(), color: piece.color },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  const pieceClass = `piece ${piece.color}-${piece.constructor.name.toLowerCase()}`;
  return (
    <div ref={drag} className={pieceClass} style={{ opacity: isDragging ? 0.5 : 1 }}></div>
  );
};

export default Piece;
