import React from 'react';
import { useDrop } from 'react-dnd';
import Piece from './Piece';
import './Square.css';

const Square = ({ piece, position }) => {
  const isDark = (position.row + position.col) % 2 === 1;
  const className = `square ${isDark ? 'dark' : 'light'}`;

  const [, drop] = useDrop({
    accept: 'PIECE',
    drop: (item) => {
      console.log(`Dropped ${item.type} at ${position.row},${position.col}`);
      // Aqui você pode implementar a lógica para manipular a peça solta
    },
  });

  return (
    <div ref={drop} className={className}>
      {piece && <Piece piece={piece} position={position} />}
    </div>
  );
};

export default Square;
