import React, { useEffect, useState } from 'react';
import Board from './components/Board';
import './App.css';

const App = () => {
  const [gameState, setGameState] = useState({
    board: Array(8).fill(null).map(() => Array(8).fill(null))
  });

  useEffect(() => {
    // Configuração inicial das peças de xadrez
    const initialBoard = Array(8).fill(null).map(() => Array(8).fill(null));
    
    // Peças brancas
    initialBoard[6] = Array(8).fill({ color: 'white', type: 'pawn' }); // peões
    initialBoard[7][0] = { color: 'white', type: 'rook' };
    initialBoard[7][1] = { color: 'white', type: 'knight' };
    initialBoard[7][2] = { color: 'white', type: 'bishop' };
    initialBoard[7][3] = { color: 'white', type: 'queen' };
    initialBoard[7][4] = { color: 'white', type: 'king' };
    initialBoard[7][5] = { color: 'white', type: 'bishop' };
    initialBoard[7][6] = { color: 'white', type: 'knight' };
    initialBoard[7][7] = { color: 'white', type: 'rook' };

    // Peças pretas
    initialBoard[1] = Array(8).fill({ color: 'black', type: 'pawn' }); // peões
    initialBoard[0][0] = { color: 'black', type: 'rook' };
    initialBoard[0][1] = { color: 'black', type: 'knight' };
    initialBoard[0][2] = { color: 'black', type: 'bishop' };
    initialBoard[0][3] = { color: 'black', type: 'queen' };
    initialBoard[0][4] = { color: 'black', type: 'king' };
    initialBoard[0][5] = { color: 'black', type: 'bishop' };
    initialBoard[0][6] = { color: 'black', type: 'knight' };
    initialBoard[0][7] = { color: 'black', type: 'rook' };

    setGameState({ board: initialBoard });
  }, []);

  return (
    <div className="App">
      <h1>Chess Game</h1>
      <Board gameState={gameState} />
    </div>
  );
};

export default App;
