import React from 'react';
import { useDrop } from 'react-dnd';
import Piece from './pieces/Piece';
import './Square.css';

const Square = ({ piece, position, movePiece }) => {
  const isDark = (position.row + position.col) % 2 === 1;
  const className = `square ${isDark ? 'dark' : 'light'}`;

  const [{ isOver }, drop] = useDrop({
    accept: 'PIECE',
    drop: (item) => {
      movePiece(item.position, position);
    },
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  });

  return (
    <div ref={drop} className={className} style={{ backgroundColor: isOver ? 'yellow' : undefined }}>
      {piece && <Piece color={piece.color} type={piece.type} position={position} />}
    </div>
  );
};

export default Square;
