import React, { useEffect, useState } from 'react';
import Board from './components/Board';
import './App.css';

const App = () => {
  const [gameState, setGameState] = useState({
    board: Array(8).fill(null).map(() => Array(8).fill(null))
  });

  useEffect(() => {
    // Configuração inicial das peças de xadrez
    const initialBoard = Array(8).fill(null).map(() => Array(8).fill(null));
    
    // Peças brancas
    initialBoard[6] = Array(8).fill({ color: 'w', type: 'pawn' }); // peões
    initialBoard[7][0] = { color: 'w', type: 'rook' };
    initialBoard[7][1] = { color: 'w', type: 'knight' };
    initialBoard[7][2] = { color: 'w', type: 'bishop' };
    initialBoard[7][3] = { color: 'w', type: 'queen' };
    initialBoard[7][4] = { color: 'w', type: 'king' };
    initialBoard[7][5] = { color: 'w', type: 'bishop' };
    initialBoard[7][6] = { color: 'w', type: 'knight' };
    initialBoard[7][7] = { color: 'w', type: 'rook' };

    // Peças pretas
    initialBoard[1] = Array(8).fill({ color: 'b', type: 'pawn' }); // peões
    initialBoard[0][0] = { color: 'b', type: 'rook' };
    initialBoard[0][1] = { color: 'b', type: 'knight' };
    initialBoard[0][2] = { color: 'b', type: 'bishop' };
    initialBoard[0][3] = { color: 'b', type: 'queen' };
    initialBoard[0][4] = { color: 'b', type: 'king' };
    initialBoard[0][5] = { color: 'b', type: 'bishop' };
    initialBoard[0][6] = { color: 'b', type: 'knight' };
    initialBoard[0][7] = { color: 'b', type: 'rook' };

    setGameState({ board: initialBoard });

    // Simulação de jogadas
    setTimeout(() => {
      const newBoard = [...initialBoard];
      // Move white pawn from e2 to e4
      newBoard[6][4] = null;
      newBoard[4][4] = { color: 'w', type: 'pawn' };
      setGameState({ board: newBoard });

      setTimeout(() => {
        const newBoard2 = [...newBoard];
        // Move black pawn from e7 to e5
        newBoard2[1][4] = null;
        newBoard2[3][4] = { color: 'b', type: 'pawn' };
        setGameState({ board: newBoard2 });
      }, 1000); // Espera 1 segundo antes de fazer a próxima jogada
    }, 1000); // Espera 1 segundo antes de fazer a primeira jogada
  }, []);

  return (
    <div className="App">
      <h1>Chess Game</h1>
      <Board gameState={gameState} />
    </div>
  );
};

export default App;
